mapper.update:
UPDATE user
SET

	 name = #{value} 
	 phone = #{value} 
	 email = #{value} 
	 birth = #{value} 

WHERE user_id = #{userId}

mapper.select:
SELECT
user_id
FROM User
WHERE name = #{name}
AND email = #{email}
AND phone =
#{phone}
LIMIT 1

SELECT *
FROM
User
WHERE name = #{name}
AND user_id = #{userId}
AND email = #{email}

mapper.update:
update user
set deleted =
#{deleted, jdbcType=BOOLEAN},
withdrawal_reason = #{withdrawalReason},
withdrawal_detail = #{withdrawalDetail}
where user_id = #{userId}

UPDATE user
SET
password = #{newPassword}
WHERE user_id = #{userId}

UPDATE inquiry
SET answer = #{answer}, status = '답변 완료' WHERE inquiry_id =
#{inquiryId}

mapper.select:
SELECT COUNT(*) 
  FROM review 
 WHERE order_item_id = #{orderItemId}

mapper.update:
UPDATE review
SET
content = #{content},
image = #{image},
rating = #{rating}
WHERE review_id = #{reviewId}

mapper.select:
SELECT point_id AS pointId, date, point, order_id AS orderId, user_id AS userId FROM point WHERE user_id = #{userId}
AND date BETWEEN #{startDate} AND #{endDate}
ORDER BY date DESC
